<script>(function(){
    let the_choices32 = document.querySelector("my-choices32");
    let seed = new Date().valueOf();
    Persistence.setItem('seed', seed);
    the_choices32.shuffle(seed);

    Persistence.setItem('answer', []); // list of selected choices32
    the_choices32.addEventListener('change', ev => {
        Persistence.setItem('answer', ev.detail);
    });
})();
