<script>(function(){
    let the_choice17 = document.querySelector("my-choice17");
    let seed = new Date().valueOf();
    Persistence.setItem('seed', seed);
    the_choice17.shuffle(seed);

    Persistence.setItem('answer', []); // list of selected choice17
    the_choice17.addEventListener('change', ev => {
        Persistence.setItem('answer', ev.detail);
    });
})();
</script>


